script.script:
var env = cm_aaca.caGetEnvironment(pDoc);
var msName = "AACA_" + env + "_MS";
var phId = cm_plm.startPlmControlPhase(pDoc.projectCode, 'MSFN', env);

try {
    Global.commitTransaction(msName);
}
catch(exp) {
   pDoc.errorOccurred = true;
   pDoc.errorCode = '99';
   pDoc.errorDescription = exp.toString();
   Global.logError(exp);
}

cm_plm.endPlmControlPhase(phId);
