script.script:
var service = cm_aaca.caGetCurrentService(pDoc, pData);
var delayForquery = pDoc.delayForQueryCommand;
var user = 'ABC';
var env = cm_aaca.caGetEnvironment(pDoc);
var resNumber = cm_aaca.caGetAvaiableRatingEngines(pDoc);
var reNumber = resNumber[pDoc.currentREIndex];
var endpoint = Global.getConfigVariable("AACA_" + env + "_ENDPOINT_RE" + reNumber);
var addInfo = env + ' - ' + reNumber + ' - ' + service;
var phId = cm_plm.startPlmControlPhase(pDoc.projectCode, 'RERW', addInfo);

try {
    var ret = cm_pws.altamiraConfiguration.executeCommand('marchaAtras', user, endpoint, service.ID, service.TABLE, delayForquery);
    if (!ret)
        Global.logDebug("Error reverting the previous configuration. Interface " + endpoint + "marchaAtras");

    ret = cm_pws.altamiraConfiguration.executeCommand('igualarGrupos', user, endpoint, service.ID, service.TABLE, delayForquery);
    if (!ret)
        Global.logDebug("Error reverting the previous configuration. Interface " + endpoint + "igualarGrupos");
} catch (E) {
        Global.logDebug("Exception reverting the previous configuration. Interface " + endpoint);
}

cm_plm.endPlmControlPhase(phId);
