    var RTree, branches, defaultTariffClasses, response;
    defaultTariffClasses = ratingTree.RTDefaultTCPerNodeArrDS.ListDS;
    branches = ratingTree.RTRatingTreeBranchArrDS.ListDS;

    for(var i=0; i<branches.length; i++){
