method.script:
select distinct {+ti+}
from CWPC_ITEMRELATION_V rpi, CWPC_ITEM pi, CWPC_ITEM ti,
     CWPC_PROJECTCOMMAND rpic, CWPC_PROJECTCOMMAND pic, CWPC_PROJECTCOMMAND tic, CWPC_PROJECTCOMMAND ic
where 1=1
      {[:itemRelationTarget] and rpi.ITEMRELATIONTARGET = ?}
      {[:relationTypeCode] and rpi.ASSOCIATIONTYPE = ?}
      and rpi.ITEMCODE=pi.ITEMCODE
      and pi.TOPITEMCODE = ti.ITEMCODE
      and rpi.STARTDATE<=SYSDATE and rpi.ITEMRELATIONVERSIONID=rpic.CATALOGOBJECTID and (rpic.ENDDATE is null or rpic.ENDDATE<=SYSDATE)
      and pi.STARTDATE<=SYSDATE and pi.ITEMCODE=pic.CATALOGOBJECTCODE and pic.OBJECTTYPE='cwt_pc:cwt_item' and (pic.ENDDATE is null or pic.ENDDATE>=SYSDATE)
      and ti.STARTDATE<=SYSDATE and ti.ITEMCODE=tic.CATALOGOBJECTCODE and tic.OBJECTTYPE='cwt_pc:cwt_item' and (tic.ENDDATE is null or tic.ENDDATE>=SYSDATE)
      and rpi.ITEMRELATIONTARGET=ic.CATALOGOBJECTCODE and ic.OBJECTTYPE='cwt_pc:cwt_item'
      and (ti.STATUS='ACT' or tic.PROJECTID=ic.PROJECTID)

this.detail = null;
this.isSearchFormVisible = true;
this.isSearchButtonVisible = true;
this.searchAction();

//if this finder is being displayed as a reference finder, return the currently selected
//document instead of displaying the detail document.
var leafName = this.model.parentLeafName;
if(this.result.selected==null) {
      this.setReferenceData(null);
      return;
}
if(this.model.refFinderDocument!=null && !this.model.refFinderDocument.isTranslation(leafName)){
   this.setReferenceData(this.result.selected[0]);
}else if (this.result.selected != null && this.result.selected.length == 1 && this.detailForm!=null){
            if(this.result.selected[0].metadata!=null){
                  this.isSearchFormVisible = false;
                  this.isSearchButtonVisible = false;
                  this.detail = new this.result.selected[0].metadata.UserInterface(this.result.selected[0]);
                  this.detail.parent = this;
                  this.detail.loadVersions(this.result.selected[0]);
                  this.detail.isAdd = false;
             }
} else {
  this.detail = null;
}

if(this.cwSelectCallback!=null){
      var args = new Array();
      this.parent.owner[this.cwSelectCallback](this.result.selected, this.result.selected!=null?this.result.selected[0]:null);
      this.parent.parent.cwChildDialogClose();
}

if (this.result.selected==null && this.result.selected.length != 1) {
      cwt_pc.showUserMessage("CWTPC0048");
    return;
}

this.detail = new cwt_pc.cwt_item.UserInterface();
this.detail.parent = this;
this.detail.model = this.result.selected[0].copyDocument(false, true);
this.detail.model.baseItemCode = this.result.selected[0].itemCode;
this.detail.model.itemCode = null;
this.detail.model.status = "DEF";
this.detail.model.ownerId = UserProfile.userProfile().positionId;
this.detail.isAdd = true;
this.isSearchFormVisible = false;
this.isSearchButtonVisible = false;

/*
for (var j = 0; j < this.result.selected.length; j++) {
      var itemFinderUI = new cwt_pc.cwt_itemFinder.UserInterface(null, this);
      itemFinderUI.search.model.baseItemCode = this.result.selected[j].itemCode;
      itemFinderUI.searchAction();
      if (itemFinderUI.result.length > 0) {
            Global.showUserMessage(Global.translateText("CWTPC0136", null, [this.result.selected[j].itemCode]));
            return;
      }
}


var finder = this.model.metadata;
for (var i = 0; i < this.result.selected.length; i++) {
    var itemVersionFinderUI = new cwt_pc.cwt_itemFinder.UserInterface(null, this);
    itemVersionFinderUI.search.model.itemCode = this.result.selected[i].itemCode;
    itemVersionFinderUI.searchAction();
    for (var j = 0; j < itemVersionFinderUI.result.length; j++) {
          itemVersionFinderUI.result[j].deleteItem();
      }
    this.result.selected[i].deleteItem();
    cwt_pc.deleteReferencedObjects(this.result.selected[i]);
}
this.searchAction();
*/
var finder = this.model.metadata;
var i;
for (i = 0; i < this.result.selected.length; i++) {
  var toDelete = this.result.selected[i];
  cwt_pc.setDeletedObjectRecord( toDelete );
  finder.operations.deleteOp.invoke(toDelete, this.invokeContext);
  cwt_pc.deleteReferencedObjects(toDelete);

  if (this.detail!= null) {
    this.detail = null;
  }
}
this.searchAction();

this.detail = new cwt_pc.cwt_item.UserInterface();
this.detail.parent = this;
this.detail.model.ownerId = UserProfile.userProfile().positionId;
this.detail.isAdd = true;
this.isSearchFormVisible = false;
this.isSearchButtonVisible = false;

this.cw$super_onInit();
this.isInitialLoad = true;
this.globalUI = new cwt_pc.globalUI(null, this);

if(this.result.selected != null && this.result.selected.length == 1) {
      return this.result.selected[0].showUsage();
}else {
      return null;
}

if( this.model.refFinderDocument != null ) {
      return false;
}else {
      return true;
}

if (this.model && this.model.parent && this.model.parent.metadata && this.model.parent.metadata instanceof cwt_pc.cwt_itemRelation) {
    if (this.model.parent.itemRelationTarget || !this.isInitialLoad) {
      return true;
    } else {
      return this.isInitialLoad==false;
    }
}


if (this.isInitialLoad) {
      return false;
} else {
      return this.cw$super_resultVisible($psCondition);
}
