script.script:
if(readDefinitions)
    theCatalog.catalogTestModeOnOff(true);

var itemCat = theCatalog.getItem(itemCode);

if(itemCat==null)
    throw ("No Catalog Item found with code " + itemCode);

var itemDoc = new Document('cm_plm.cwt_item', itemCode);
itemDoc.endDate = itemCat.getItemEndDate();

if(readDefinitions)
    theCatalog.catalogTestModeOnOff(false);

return itemDoc;
