script.script:
var existingItem = cm_common.getItemByCode(entityData.specificationCode);
if(existingItem) {
    var fault = api_common.createFault("E105", "The item already exists.");
    return fault;
}

var response = cm_common.validateItemAvailability(entityData, projectCode);
if(api_common.isFault(response))
    return response;

response = cm_common.createSpecificationByValue(entityData,projectCode); //recursively Saves or Modify the catalog entities
if(api_common.isFault(response)) {
    Global.rollbackTransaction("CATALOG");
    return response;
}

cm_plm.setTopItemCode(entityData.specificationCode, projectCode);

var ord = cm_plm.getPLMOrderFromProjectCode(projectCode);
if(ord!=null)
    cm_plm.registerItemInOrder(entityData.specificationCode, projectCode, 'INITIAL');

return response;
