script.script:
try{

    var PA = new cm_plm.projectAssignment();
    PA.projectCode = projectCode;
    var list = cm_plm.queryProjectAssignment(PA);
    var update = false;
    var object, objectDoc;

    for(var i=0; i<list.length; i++){
        object = new DataStructure(list[i].objectType);
        object.objectId = list[i].objectId;
        objectDoc = cm_plm.getObjectDoc(object);
        if(api_common.isFault(objectDoc))
            return objectDoc;

        if((list[i].endDate && (!objectDoc.endDate || objectDoc.endDate < list[i].endDate))){

            objectDoc.endDate = list[i].endDate;
            objectDoc.save();
        }
    }

}catch(exp){
    return api_common.createException(exp, "ERR");
}
