script.script:
try{
    var doc = new cm_plm.projectAssignmentDoc();
    projectAssignment.mapTo(doc);
    doc.lastUpdatedTime = new Date();
    doc.orderNum = Global.nextInSequence("NGAA_PROJECTASSIGNMENT_SEQ");
    doc.save();
    doc.mapTo(projectAssignment);
    return projectAssignment;

}catch(exp){
    return api_common.createException(exp, "ERR");
}
