script.script:
try{
    //we first remove the projectAssignment recorde for this object
    var PA = new cm_plm.projectAssignment();
    PA.objectId = object.objectId;
    var list = cm_plm.queryProjectAssignment(PA);
    if(api_common.isFault(list))
        return list;

    var result;
    for(var i=0; i<list.length; i++){
        result = cm_plm.removeProjectAssignmentByKey(list[i]);
        if(api_common.isFault(result))
            return result;
    }

    //now we remove the object itself
    var doc = Document.readDoc(modelType, object.id);
    if(!doc)
        return api_common.createFault("ERR", "The object was not found: " + object.id + "[" + modelType + "]");

    doc.deleteFromDB();

    return object;
}catch(exp){
    return api_common.createException(exp, "ERR");
}
