script.script:
try{
    if(cm_plm.isProjectActive(projectCode)){
        Global.runDBUpdate("CATALOG", "delete from NGAA_PROJECTASSIGNMENT where projectCode = '" + projectCode + "'" );
        Global.runDBUpdate("CATALOG", "delete from NGAA_APPBOLTONTYPEGROUP where projectCode = '" + projectCode + "'" );
        Global.runDBUpdate("CATALOG", "delete from NGAA_DESTTYPEASSOTARIFFCLASS where projectCode = '" + projectCode + "'" );
        Global.runDBUpdate("CATALOG", "delete from NGAA_NX24APPBOLTONTYPE where projectCode = '" + projectCode + "'" );
        Global.runDBUpdate("CATALOG", "delete from NGAA_NX24APPBOLTONTYPELIST where projectCode = '" + projectCode + "'" );
    }else
        return api_common.createFault("ERR", "Projects in ACT state cannot be deleted!");

}catch(exp){
    return api_common.createException(exp, "ERR");
}
