script.script:
//see if there are any groupIds which are not used in Nx24 table, then add them into the list
var finder = new cm_plm.unusedGroupIdFinder();
finder.searchDocument.endDate = null;
var list = finder.search();

if(!reusableGroupIds)
    reusableGroupIds = new Array();

//clear the current list;
while(reusableGroupIds.length > 0)
    reusableGroupIds.pop();

for(var i=0; i< list.length; i++)
    reusableGroupIds.push(list[i].AppBoltonTypeGroupId);

return reusableGroupIds;
