script.script:
try{
    var finder = new cm_plm.projectAssignmentFinder();
    projectAssignment.mapTo(finder.searchDocument);
    var result = finder.search();
    var list = new DataObjectList();
    for(var i=0; i<result.length; i++){
        var ds = new cm_plm.projectAssignment();
        result[i].mapTo(ds);
        list.addLast(ds);
    }

    return list;

}catch(exp){
    return api_common.createException(exp, "ERR");
}
