script.script:
/**
 * Read the publish reply into into the process document
 *
 * @author  Luis Barreira
 * @date    20-02-2014
 *
 * @param {Number} pDoc    The process document
 * @param {Number} doc     The interface document\data structure
 */
var pDoc = process.processDocument;
pDoc.caReturnType = doc.returnType;
pDoc.caReturnMessage = doc.errorMessage;

if(pDoc.caReturnType!=null && pDoc.caReturnType!='SUCCESS') {
    var ord = process.processOrder;
    ord[0].errorList = pDoc.caReturnMessage;
    ord.save();
}
