script.script:
try{

    var doc = Document.readDoc("cm_plm:projectAssignmentDoc", projectAssignment.projectAssignmentId);
    if(!doc)
        return api_common.createFault("ERR", "The projectAssignmentId was not found: " + projectAssignment.projectAssignmentId);

    doc.deleteFromDB();

    return projectAssignment;

}catch(exp){
    return api_common.createException(exp, "ERR");
}
