try{
    if(!cm_plm.isModificationAllowed(Nx24AppBoltonType))
        return api_common.createFault("ERR", "This modality is being maintained in another project.");

    if(cm_plm.isProjectActive(Nx24AppBoltonType.projectCode))
        return api_common.createFault("ERR", "Item cannot be created for projects in ACT status.");

    var result;

    if(cm_plm.isObjectActive(Nx24AppBoltonType)){
        Nx24AppBoltonType.endDate = new Date();
        result = cm_plm.endDateObject(Nx24AppBoltonType);
        if(api_common.isFault(result))
            return result;

        //now we create a new version for this object
        Nx24AppBoltonType.endDate = null;
        Nx24AppBoltonType.startDate = new Date();
        result = cm_plm.createNx24AppBoltonTypeByValue(Nx24AppBoltonType);
    }else{

        var doc = Document.readDoc("cm_plm:Nx24AppBoltonTypeDoc", Nx24AppBoltonType.id);
        if(!doc)
            return api_common.createFault("ERR", "The GroupId was not found: " + Nx24AppBoltonType.Nx24AppBoltonType);

        Nx24AppBoltonType.mapTo(doc);
        doc.save();
        doc.mapTo(Nx24AppBoltonType);

        result = cm_plm.updateProjectAssignmentForObject(Nx24AppBoltonType);
    }

    if(api_common.isFault(result))
        return result;

    return Nx24AppBoltonType;

}catch(exp){
    return api_common.createException(exp, "ERR");
}
