script.script:
try {
    var OP_REFERENCE = 'DELETE_ENTITY';
    var actionCommonInfo = input.actionCommonInfo;
    var entity = input.oCSEntity;

    var projectCode = actionCommonInfo.sessionId;
    var user = actionCommonInfo.userCode; //TODO: login user

    var project = epc.getProjectByCode(projectCode);
    if(!project)
        return  api_common.createFault("E101", "Invalid SessionId.");    //TODO: correct code

    var entityDs =cm_pws.getInnerDataStructure(entity);

    var entityData = cm_pws.pwsCreateEntity(entityDs, project.projectCode);

    var res = cm_pws.services.pwsMap(entityDs, entityData);
    if(api_common.isFault(res)) {
        return res;
    }

    var response = cm_common.plmRemoveItem(entityData,project.projectCode);
    if(api_common.isFault(response))
        return response;

    var output = new OCSConfigurationOperation.v1.types.DeleteEntityResponse();
    response = cm_pws.services.pwsMap_ActionCommonInfoResult(output.actionCommonInfoResult, OP_REFERENCE, projectCode);
    if(api_common.isFault(response))
        return response;

    return output;
} catch (exp)
{
    var code = eval("arguments.callee.name");
    var fault = api_common.createException(exp, code);    //TODO: correct error code
    return fault;
}
