script.script:
try
{
    var configSessionFinder = new cm_pws.configSessionFinder();
    configSessionQuery.configSession.mapTo(configSessionFinder.searchDocument);
    configSessionFinder.searchDocument.maxRows = configSessionQuery.maxRows;
    var searchResults = configSessionFinder.search();

    var results = new cm_pws.configSessionList();
    for(var i=0; i< searchResults.length; i++)
    {
        var configSession = new cm_pws.configSession();
        searchResults[i].mapTo(configSession);
        results.configSession[i] = configSession;
    }

    return results
}
catch (exp)
{
    var code = eval("arguments.callee.name");
    var fault = api_common.createException(exp, code);    //TODO: correct error code
    return fault;
}
