script.script:
try {
    var number = null;

    if(inputValue == null || inputValue == "")
        return 0;
    else {
        var y = parseInt(inputValue);
        if (isNaN(y)) {
            var fault = api_common.createFault("E123", "Te received value is not a number.");    //Correct error code
            return fault;
        }
        if(y < 0) {
            var fault = api_common.createFault("E123", "Te received is not a positive number.");    //Correct error code
            return fault;
        }

        return y;
    }
}
catch (exp)
{
    var code = eval("arguments.callee.name");
    var fault = api_common.createException(exp, code);    //TODO: correct error code
    return fault;
}
