script.script:
var output = new cm_pws.test.service.output();

var fileName =  cm_pws.test.generateLogFileName();
var fileReader = new FileReader(fileName);

while(!fileReader.isEOF())
{
    output.result += fileReader.readLine() + "\n";
}
if(!output.result)
    Global.throwException("The test result could not be read!");

return output;
