var rtbrtArrDS = this.RTBRTNodeOutputArrDS.ListDS;
var str = "";

for ( var i = 0; i < rtbrtArrDS.length; i++ ){
    var listDS = rtbrtArrDS[i].RTNOTonArrDS.List;
    var lengthDS = listDS.length;
    for ( var j = 0; j < lengthDS; j++){
        if ( j > 0 )
            str = str + ";";
         var s = new epc.data.Specification();
        s.project =  cwa_epc.getDefaultProj();
        s.specificationCode = listDS[j];

        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
                str = str + r.TonCode;
        }


    }
}
return str;

