var str = "";

for ( var i = 0; i < rtbrtArrDS.length; i++ ){
    var nodeName = rtbrtArrDS[i].NodeName;
    var doc = Document.readDoc("cm_spoc.RatingTreeNodeTypes",nodeName);
    var att = null;
    if (doc.nodeType == 'att'){

        for ( var i = 0; i < rtbrtArrDS.length; i++ ){
            if(rtbrtArrDS[i].NodeName == 'G'){
                var listDS = rtbrtArrDS[i].RTNodeOutputArrDS.ListDS;
                var lengthDS = listDS.length;
                for ( var j = 0; j < lengthDS; j++){
                    if ( j > 0 )
                        str = str + ";";
                    str = str + listDS[j].NodeValue;
                }
            }
        }

    }
}
return str;
