method.script:
var item;
var res = new DataObjectList;
var ctCodes = null;
var ctGroupCodes = null;
if(searchData.codeTable || searchData.groupCodeTable){
    if(!searchData.codeTable.toString().equals("false") &&
        !searchData.groupCodeTable.toString().equals("false")){
        ctCodes = Global.getCTCodesAndDescriptions(searchData.codeTable);
        ctGroupCodes = Global.getCTCodesAndDescriptions(searchData.groupCodeTable);
        for(var i=0; i<ctCodes.length; i=i+2){
            item = new cwa_epc.itemSubTypeResultDoc;
            item.id = ctCodes[i];
            item.itemSubtype = ctCodes[i+1];
            for(var j=0; j<ctGroupCodes.length; j=j+2){
                if(ctCodes[i]==ctGroupCodes[j])
                    res.addLast(item);
            }
        }
    }
    else if(!searchData.codeTable.toString().equals("false")){
        ctCodes = Global.getCTCodesAndDescriptions(searchData.codeTable);
        for(var i=0; i<ctCodes.length; i=i+2){
            item = new cwa_epc.itemSubTypeResultDoc;
            item.id = ctCodes[i];
            item.itemSubtype = ctCodes[i+1];
            res.addLast(item);
        }

    }
    else{
        ctGroupCodes = Global.getCTCodesAndDescriptions(searchData.groupCodeTable);
        for(var i=0; i<ctGroupCodes.length; i=i+2){
            item = new cwa_epc.itemSubTypeResultDoc;
            item.id = ctGroupCodes[i];
            item.itemSubtype = ctGroupCodes[i+1];
            res.addLast(item);
        }
    }
}else if(searchData.referenceData){
    var dtCodes = Global.getCodes(searchData.referenceData);
    if(dtCodes){
        for(var i=0; i<dtCodes.length; i++){
            item = new cwa_epc.itemSubTypeResultDoc;
            item.id = dtCodes[i];
            item.itemSubtype = Global.getCodeDescription(searchData.referenceData,item.id);
            res.addLast(item);
        }
    }
}



return res;
