function validateCodesInMessage(){
    var codeArrayInMsg = new Array();
    var codeInTheMsg;
    var found;
    if(codesInMessage){
        for(var i = 0 ; i < codesInMessage.length; i ++){
            found = false;
            codeInTheMsg = codesInMessage[i].replace(/<</g,'');
            codeInTheMsg = codeInTheMsg.replace(/>>/g,'');
            codeArrayInMsg.push(codeInTheMsg);
            for(var a = 0; a < codeArray.length; a++){
                if(codeArray[a][0] == codeInTheMsg)
                    found = true;
            }
            if(!found){
                var size = findSize(codeInTheMsg);
                if(size && size > 0){
                    var newCode = new Array();
                    newCode = [codeInTheMsg,size];
                    codeArray.push(newCode);
                }
                else{
                    textAreaEnglish = replaceAll("<<"+codeInTheMsg+">>",'',textAreaEnglish);
                }
            }
        }
    }

    for(var x = 0 ; x < codeArray.length; x++){
        if(codeArrayInMsg.indexOf(codeArray[x][0]) == -1)
            codeArray.splice(x);
    }
}

function calculateRealLength(){
    var codesSize = 0;
    for(var i = 0 ; i < codeArray.length; i++){
        codesSize += parseInt(codeArray[i][1]);
    }
    var msgWithoutCodes = textAreaEnglish.replace(/<<(.*?)>>/g, "");
    msgWithoutCodes = msgWithoutCodes.replace("  "," ");
    msgWithoutCodes = msgWithoutCodes.replace("   "," ");

    return msgWithoutCodes.length + codesSize;
}

function findSize(code){
    var finder = new Finder("cm_spoc.patronSQLFinder");
    finder.searchDocument.cod_patronmensa = code;
    var result = finder.search();

    if(result && result.length > 0)
        return result[0].max_size;

    return 0;
}

function replaceAll(find, replace, str) {
  return str.replace(new RegExp(find, 'g'), replace);
}

