function updateStructure(DS,text){
    var exists = false;

    for(var b = 0 ; b < DS.length; b++){
        if(DS[b].MessageTextLanguage == 'German'){
            DS[b].MessageTextCode     = text;
            exists = true;
        }
    }
    if(!exists){
        var DSLength = DS.length;
        DS[DSLength] = cm_spoc.initInnerDS("base_MessageText");
        DS[DSLength].MessageTextLanguage = 'German';
        DS[DSLength].MessageTextCode     = text;
    }
}

