function validate(text){
    var count;
    var validWords = text.split(" ");
    for(var i = 0 ; i<validWords.length; i ++){
        if(validWords[i].substring(0,2) == '<<' && validWords[i].substring(validWords[i].length-2,validWords[i].length) != '>>')
            return false;
        else if(validWords[i].substring(0,2) != '<<' && validWords[i].substring(validWords[i].length-2,validWords[i].length) == '>>')
            return false;
        count = validWords[i].match(/>>/g);
        if(count && count.length > 1 )
            return false;
        count = validWords[i].match(/<</g);
        if(count && count.length > 1 )
            return false;
    }
    return true;
}

