method.script:
/*var pl = new cm_spoc.PickList(null,this);
var itemFinder = new Finder("cm_spoc.tariffPlanPickListFinder");
pl.availableItems = itemFinder.search();

return pl;*/


var itemFinder = new Finder("cm_spoc.catalogItemFilteredFinder");
itemFinder.searchDocument.baseItemCode = "base_Rin";
var result = itemFinder.search();
return cm_spoc.initPickList(this,result, this.parent.model.SubscriberRINCriterionDS.SRINCriterionRinArrDS.List,"itemCode","itemCode");

//this.model.TariffPlanCriterionDS.TPCriterionTPArrDS = cm_spoc.initInnerDS("base_TPCriterionTPArr");
for(var i=0;i<this.pickList.selectedItems.length;i++){
    var item = this.pickList.selectedItems[i];
    this.model.SubscriberRINCriterionDS.SRINCriterionRinArrDS.List[i] = this.pickList.selectedItems[i].id;
}
this.parent.owner.model.SubscriberRINCriterionDS.SRINCriterionRinArrDS = this.model.SubscriberRINCriterionDS.SRINCriterionRinArrDS;

if(this.pickList.selectedItems.length>0){
    if(this.parent.owner.ListSelected==null)
        this.parent.owner.ListSelected = new DataObjectList();
    //Check if the label was added
    var exist = false;
    for(var j=0; j< this.parent.owner.ListSelected.length; j++){
        if( this.parent.owner.ListSelected[j].description == "Subscriber's RIN"){
             exist=true;
        }
    }
    if(!exist){
        var item = new cm_spoc.FinderSelectReferenceDS();
        item.description = "Subscriber's RIN";
        this.parent.owner.ListSelected.addLast(item);
        this.parent.owner.ListSelected.updateList;
    }
}else{
    if(this.parent.owner.ListSelected==null)
        this.parent.owner.ListSelected = new DataObjectList();
    for(var k=0; k< this.parent.owner.ListSelected.length; k++){
        if( this.parent.owner.ListSelected[k].description == "Subscriber's RIN")
             this.parent.owner.ListSelected.delete(k);
    }
    this.parent.owner.ListSelected.updateList;
}
this.parent.cwDialogClose();
