method.script:
//
var itemFinder = new Finder("cm_spoc.catalogItemFilteredFinder");
itemFinder.searchDocument.baseItemCode = "base_PDTEWalletArr";
var result = itemFinder.search();

var pl = cm_spoc.initPickList(this,result,this.model.PDTEWalletArrDS.List,"itemCode","itemCode");
pl.modelCallback = this.setPDTEWalletArrPLModel;
return pl;

//
var itemFinder = new Finder("cm_spoc.catalogItemFilteredFinder");
itemFinder.searchDocument.baseItemCode = "base_PDTEAnalysedConceptsArr";
var result = itemFinder.search();

var pl = cm_spoc.initPickList(this,result,this.model.PDTEAnalysedConceptsArrDS.List,"itemCode","itemCode");
pl.modelCallback = this.setPDTEAnalysedConceptsArrPLModel;
return pl;

//2014.08.08 - ecozzuto
var model=this.model;
if (!model.specificationCode){
    model = cm_spoc.initModelForCreation(model);
    model.GeneralTimeOrDates='G';
    model.WeeklyOrMonthly='W';
}else{
    model = cm_spoc.initModelForModification(model);
}
return model;
