var itemFinder = new Finder("cm_spoc.catalogItemFilteredFinder");
itemFinder.searchDocument.baseItemCode = "base_Wallet";
var result = itemFinder.search();

var pl = cm_spoc.initPickList(this,result,this.model.TPSpecificationWalletArrDS.List,"itemCode","itemCode");
pl.modelCallback = this.setWalletPL;
return pl;

