script.script:
if( cm_spoc.getExistingItem(data.specificationCode) )
   return false;

var itemFinder = new Finder("cm_spoc.GenericUniqueIDFinder");
itemFinder.searchDocument.defaultValue = data[itemAttributeCode];
itemFinder.searchDocument.baseItemCode = baseItemCode;
itemFinder.searchDocument.itemAttributeCode= itemAttributeCode;
var result = itemFinder.search();
if(result.length > 0)
{
    return false;
}
return true;
