var s="";
if(code.length == 12){

    if(code == "111111111111") {s= "All"}

    else if(code == "000000000000"){s="None"}

    else{
        for(var i=0; i< code.length; i++){

            if(code[i]==1){s +=(cm_spoc.getMonthName(i)+" ")}

        }


    }
}
if(code.length ==31){


    if(code == "1111111111111111111111111111111") {s= "All"}
    else if (code == "0000000000000000000000000000000") {s="None"}

    else{
        for(var i=0; i< code.length; i++){

            if(code[i]==1){s+=i+1+" "}

        }
    }
}


if(code.length== 24){


    if(code == "111111111111111111111111") {s= "All"}
    else if(code == "000000000000000000000000") {s= "None"}
    else{
        for(var i=0; i< code.length; i++){

            if(code[i]==1){s+=i+":00"+" "}


        }

    }



}

if(code.length== 7){

    if(code == "1111111") {s= "All"}
    else if(code == "0000000") {s= "None"}
    else{
        for(var i=0; i< code.length; i++){

