script.script:
if (!finderUi.parent.model.readonly) {

    var sc = finderUi.result.selected[0].specificationCode;


    //ui.parent.model : RatingTree

    for (var i=0;i<finderUi.parent.model.RTRatingTreeBranchArrDS.ListDS.length;i++) {
        if(finderUi.parent.model.RTRatingTreeBranchArrDS.ListDS[i].specificationCode == sc ){

            var rtbui = new cm_spoc.forms.RatingTreeBranchForm(finderUi.parent.model.RTRatingTreeBranchArrDS.ListDS[i],finderUi.parent);

            finderUi.parent.branchForm = rtbui;
        }
    }

} else {
    return;
}
