script.script:
var result=ui.parent.owner.result;

result = new DataObjectList();

var UCArr;
if(ui.parent.owner.parent.positive){
UCArr=ui.parent.owner.parent.model.UCPositiveConUCACArrDS;
}else{
UCArr=ui.parent.owner.parent.model.UCNegativeConUCACArrDS;
}

for ( var i=0; i < UCArr.ListDS.length; i++) {

    result[i]=UCArr.ListDS[i];
}

mapToOutputDoc(result);

ui.parent.owner.model.updateUI()
ui.parent.owner.model.setList(result);
 ui.model.UCSimAppConditStaticRelEnum=null;


function mapToOutputDoc(result) {

    for (var t=0;t<result.length;t++ ) {
        var doc = new cm_spoc.UCSimpleAccumulationConditionsDoc();
        result[t]=result[t].mapTo(doc,"cm_spoc.SimpleApplicabilityConditionsCM")[0];

    }
}
