script.script:
var priceEventField="";

switch (priceEvent){
    case "BoltOnSubscription":
    case "BoltOnUnsubscription":
    priceEventField =  "BoltOnPriceEvent";
    break;

    case "SubscriptionList":
    case "AddMemberList":
    case "ModifyMemberList" :
    case "ActDeactList" :
    case "UnsubscriptionList" :
    case "RemoveMemberList" :
    case "MassiveUpdateList" :
    priceEventField =  "ListPriceEvent";
    break;


    case "SubscriptionTribe" :
    case "UnsubscriptionTribe" :
    priceEventField =  "TribePriceEvent";
    break;

    case "UnsubscriptionUC" :
    case "SubscriptionUC" :
    case "ModifyUC" :
    priceEventField =  "UsageControlPriceEvent";
    break;

    case "UnsubscriptionRecurrent" :
    case "SubscriptionRecurrent" :
    priceEventField =  "RecurrentSubscriptionPriceE";
    break;

    case "SubscriptionServiceTP" :
    case "UnsubscriptionServiceTP" :
    priceEventField =  "ServiceTariffPlanPriceEvent";
    break;

    case "SubscriptionTPChange" :
    priceEventField =  "TariffPlanChangePriceEvent";
    break;

    case "SubscriptionPromoProf" :
    case "UnsubscriptionPromoProf" :
    priceEventField =  "PromotionalProfilePriceEven";
    break;

    case "RecurrentPhase" :
    priceEventField =  "RecurrentPhasePriceEvent";
    break;


}

return priceEventField;
