script.script:
//0: singular, simple relation
//1: plural, simple relation
//2: plural, complex relation
//3: singular, complex relation
//4: plural, recursive tree
var cspec = spec.copy();    //test node[0] may increase array length if node[0] does not exist
var node = cspec[relationType];
if (node == null || !(node instanceof DataStructure)) {
    return 0;
} else {
    if (node.isCollection()) {
        if (!(node[0] instanceof DataStructure)) {
            return 1;
        } else if (node[0].isBasedOn("epc.data.treeElement")) {
            return 4;
        } else if (node[0].isBasedOn("epc.data.Association") || "CW_REFID" in node[0]) {
            return 2;
        } else {
            return -1;    //invalid spec
        }
    } else {
        return 3;
    }
}
