script.script:
if (model.isBasedOn("epc.data.ResourceSpecification")) {
    return "ResourceSpecification";
} else if (model.isBasedOn("epc.data.ProductSpecification")) {
    return "ProductSpecification";
} else if (model.isBasedOn("epc.data.ServiceSpecification")) {
    return "ServiceSpecification";
} else if (model.isBasedOn("epc.data.ProductOffering")) {
    return "ProductOffering";
} else if (model.isBasedOn("epc.data.ConsumptionSpecification")) {
    return "ConsumptionSpecification";
} else if (model.isBasedOn("epc.data.BalanceSpecification")) {
    return "BalanceSpecification";
} else if (model.isBasedOn("epc.data.ChargeTypeEpc")) {
    return "PricePlan";
} else
    return cm_spoc.getValue(model,"specificationType");

