script.script:
if (!model.specificationCode){
    var simpleApplicabilityCondArr = cm_spoc.initInnerDS("base_SimpleApplicabilityCondArr");
    model.SimpleApplicabilityCondArrDS = simpleApplicabilityCondArr;

}else{
    var s = new epc.data.Specification();
    s.project = cwa_epc.getDefaultProj();



    s.specificationCode = model.SimpleApplicabilityCondArr;
    var r = epc.getSpecificationByKey(s);
    if(!api_common.isFault(r)){
       model.SimpleApplicabilityCondArrDS=r;
    }


    var simpCondRel = model.SimpleApplicabilityCondArrDS.List;
    var simpCondDS = model.SimpleApplicabilityCondArrDS.ListDS;
    for (var i=0;i<simpCondRel.length;i++){
        s.specificationCode = simpCondRel[i];
        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
            r.TariffClassAppConditionEnum = r.TariffClassAppCondition;
            r.DestinationTypeCondEnum = r.DestinationTypeAppCondition;
            r.TemporalConditionEnum = r.TemporalAppCondition;
            simpCondDS[i]=r;
        }
    }
}
return model;
