script.script:
if(!model.specificationCode){

    var awPrSegmentArr = cm_spoc.initInnerDS("base_APGAwPromSegmArr");
    model.APGAwPromSegmArrDS = awPrSegmentArr;


    return model;
}
else{
   var s = new epc.data.Specification();
   s.project =  cwa_epc.getDefaultProj();

   s.specificationCode = model.APGAwPromSegmArr;
   var r = epc.getSpecificationByKey(s);
   if(!api_common.isFault(r)){
        model.APGAwPromSegmArrDS=r;
         }

   var awPrSegmentRel= model.APGAwPromSegmArrDS.List;
   var awPrSegmentDS= model.APGAwPromSegmArrDS.ListDS;
   for(var i=0;i<awPrSegmentRel.length;i++){
       s.specificationCode = awPrSegmentRel[i];
       var r = epc.getSpecificationByKey(s);
       if(!api_common.isFault(r)){
           awPrSegmentDS[i]=r;
       }
   }

   return model;
}
