script.script:
if(!model.specificationCode){
    var CntCodeArr = cm_spoc.initInnerDS("base_CntCodeArr");
    model.CntCodeArrDS = CntCodeArr;

    return model;
}
else{
    var s = new epc.data.Specification();
    s.project =  cwa_epc.getDefaultProj();

    s.specificationCode = model.CntCodeArr;
    var a = epc.getSpecificationByKey(s);

     if(!api_common.isFault(a)){
       model.CntCodeArrDS=a;
     }

    var CntCodeRel =  model.CntCodeArrDS.List;
    var CntCodeDS= model.CntCodeArrDS.ListDS;

    for(var i=0;i<CntCodeRel.length;i++){
        s.specificationCode = CntCodeRel[i];
        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
            CntCodeDS[i]=r;
        }
    }
    return model;
}
