script.script:
if(!model.specificationCode){
    var ExtNotCommErrorArr = cm_spoc.initInnerDS("base_ExtNotCommErrorArr");
    model.ExtNotCommErrorArrDS = ExtNotCommErrorArr;

    return model;
}
else{
    var s = new epc.data.Specification();
    s.project =  cwa_epc.getDefaultProj();

    var ExtNotCommErrorArrRel = model.ExtNotCommErrorArr;
    s.specificationCode = ExtNotCommErrorArrRel;
    var a = epc.getSpecificationByKey(s);
    if(!api_common.isFault(a)){
        model.ExtNotCommErrorArrDS = a;
    }

    return model;
}
