script.script:
if(!model.specificationCode){



   var itemFinder = new Finder("cm_spoc.TonFinder");
   var result = itemFinder.search();
   if(result.length>0){
       var btCode=result[0].itemCode;
       model.TonEnum=btCode;
       model.TonContentInputFor = btCode;
   }


 }else{

   //Assign the values of the model to the proper select field
   model.TonEnum =  model.TonContentInputFor;


}
return model;
