script.script:
if(!model.specificationCode){

    var caLifCycleTransitionArr = cm_spoc.initInnerDS("base_CALifCycleTransitionArr");
    model.CALifCycleTransitionArrDS = caLifCycleTransitionArr;


    return model;
}
else{
   var s = new epc.data.Specification();
   s.project =  cwa_epc.getDefaultProj();

   s.specificationCode = model.CALifCycleTransitionArr;
   var r = epc.getSpecificationByKey(s);
   if(!api_common.isFault(r)){
        model.CALifCycleTransitionArrDS=r;
         }

   var caLifeCycleRel= model.CALifCycleTransitionArrDS.List;
   var caLifeCycleDS= model.CALifCycleTransitionArrDS.ListDS;
   for(var i=0;i<caLifeCycleRel.length;i++){
       s.specificationCode = caLifeCycleRel[i];
       var r = epc.getSpecificationByKey(s);
       if(!api_common.isFault(r)){
           caLifeCycleDS[i]=r;
       }
   }

   return model;
}
