script.script:
if(!model.specificationCode){

   var itemFinder = new Finder("cm_spoc.contentTypeFinder");
   var result = itemFinder.search();
   if(result.length>0){
       var btCode=result[0].itemCode;
       model.ContentTypeEnum=btCode;
       model.ContentType = btCode;
   }


 }else{

   //Assign the values of the model to the proper select field
   model.ContentTypeEnum = model.ContentType;
}
return model;
