script.script:
if(ui.parent.parent.positive){
var listDS= ui.parent.parent.model.UCPositiveConUCACArrDS.ListDS;
}else{
var listDS= ui.parent.parent.model.UCNegativeConUCACArrDS.ListDS;
}





var sel = ui.parent.result.selected[0].id;

for( var i=0;i<listDS.length;i++){
      if(listDS[i].id == sel){
            ui.model = listDS[i];
       ui.parentIndex = i;
            break;
      }
}

return  ui.model;
