script.script:
if(!model.specificationCode){

    var LCSSLifeCycleArr = cm_spoc.initInnerDS("base_LCSSLifeCycleArr");
    model.LCSSLifeCycleArrDS = LCSSLifeCycleArr;

    return model;
}
else{
   var s = new epc.data.Specification();
   s.project =  cwa_epc.getDefaultProj();

   s.specificationCode = model.LCSSLifeCycleArr;
   var r = epc.getSpecificationByKey(s);
   if(!api_common.isFault(r)){
        model.LCSSLifeCycleArrDS=r;
         }

   var LCSSLifeCycleArrRel= model.LCSSLifeCycleArrDS.List;
   var LCSSLifeCycleArrDS= model.LCSSLifeCycleArrDS.ListDS;
   for(var i=0;i<LCSSLifeCycleArrRel.length;i++){
       s.specificationCode = LCSSLifeCycleArrRel[i];
       var r = epc.getSpecificationByKey(s);
       if(!api_common.isFault(r)){
           LCSSLifeCycleArrDS[i]=r;
       }
   }

   return model;
}
