script.script:
if(ui.model.specificationCode){
 for(var i=0; i<ui.model.ListAllowedStateArrDS.List.length; i++){

        var found = false;

        for(var j=0;j<ui.ListSettingsPickList.availableItems.length;j++){
        var item = ui.ListSettingsPickList.availableItems[j];
            if(item.id == ui.model.ListAllowedStateArrDS.List[i]){
                found = true;
                break;
            }
        }

        if(found){
            ui.ListSettingsPickList.selectedItems.addLast(item);
            ui.ListSettingsPickList.availableItems.remove(item);
        }
    }

}
