script.script:
if(!model.specificationCode){
    var MSMSPriceGuidingArr = cm_spoc.initInnerDS("base_MSMSPriceGuidingArr");
    model.MSMSPriceGuidingArrDS = MSMSPriceGuidingArr;

    return model;
}
else{
    var s = new epc.data.Specification();
    s.project =  cwa_epc.getDefaultProj();

    s.specificationCode = model.MSMSPriceGuidingArr;
    var r = epc.getSpecificationByKey(s);
    if(!api_common.isFault(r)){
       model.MSMSPriceGuidingArrDS=r;
    }

    var MSMSPriceGuidingRel =  model.MSMSPriceGuidingArrDS.List;
    var MSMSPriceGuidingArrDS =  model.MSMSPriceGuidingArrDS.ListDS;
    for(var i=0;i<MSMSPriceGuidingRel.length;i++){
        s.specificationCode = MSMSPriceGuidingRel[i];
        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
            MSMSPriceGuidingArrDS[i]=r;
            MSMSPriceGuidingArrDS[i].PriceEnum = MSMSPriceGuidingArrDS[i].Price;//load the values into enumeration variable
        }
    }
    return model;
}
