script.script:
var model = ui.model;
if (!model) {
    model = new DataStructure("cm_spoc.data.ModalityDestinationList");
}
if (!model.specificationCode) {
    model = cm_spoc.initModelForCreation(model);
}
else {
    model = cm_spoc.initModelForModification(model);
    this.DesModalityIDEnum=this.model.DesModalityIDEnum;
    this.CustomerProfileEnum=this.model.CustomerProfileEnum;
    this.CauseEnum=this.model.CauseEnum;
}
return model;
