ui.monthsIterator =  new Array();

var monthlyResultArray = cm_spoc.getMonthlyResultArray(result);


for (var m = 0; m<12;m++){

    ui.monthlyResultElement = monthlyResultArray[m];
    var fui =  new cm_spoc.forms.MonthForm(monthlyResultArray[m],ui);
    fui.month = cm_spoc.getMonthName(m);
    fui.result = monthlyResultArray[m];
    ui.monthsIterator.push(fui);

}
