script.script:
if(!model.specificationCode){
var ConstraintsCByPlanOrTArr = cm_spoc.initInnerDS("base_ConstraintsCByPlanOrTArr");
model.ConstraintsCByPlanOrTArrDS = ConstraintsCByPlanOrTArr;

return model;
}
else{
    var s = new epc.data.Specification();
    s.project =  cwa_epc.getDefaultProj();

    var ConstraintsCByPlanOrTArrRel = model.ConstraintsCByPlanOrTArr;
    s.specificationCode = ConstraintsCByPlanOrTArrRel;
    var a = epc.getSpecificationByKey(s);
    if(!api_common.isFault(a)){
        model.ConstraintsCByPlanOrTArrDS = a;
        ConstraintsCByPlanOrTArrRel = a.List;
    }
    var ConstraintByPlanOrTechnologDS = model.ConstraintsCByPlanOrTArrDS.ListDS;
    for(var i=0;i<ConstraintsCByPlanOrTArrRel.length;i++){
        s.specificationCode = ConstraintsCByPlanOrTArrRel[i];
        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
            ConstraintByPlanOrTechnologDS[i]=r;
             var SubOperationRel = r.SubOperation;
             s.specificationCode = SubOperationRel;
             var c =  epc.getSpecificationByKey(s);
             if(!api_common.isFault(c)){
                 ConstraintByPlanOrTechnologDS[i].SubOperationDS = c;
            }
        }
    }
    return model;
}
