script.script:
if(!model.specificationCode){
var ConstraintsCByStatusArr = cm_spoc.initInnerDS("base_ConstraintsCByStatusArr");
model.ConstraintsCByStatusArrDS = ConstraintsCByStatusArr;

return model;
}
else{
    var s = new epc.data.Specification();
    s.project =  cwa_epc.getDefaultProj();

    var ConstraintsCByStatusArrRel = model.ConstraintsCByStatusArr;
    s.specificationCode = ConstraintsCByStatusArrRel;
    var a = epc.getSpecificationByKey(s);
    if(!api_common.isFault(a)){
        model.ConstraintsCByStatusArrDS = a;
        ConstraintsCByStatusArrRel = a.List;
    }
    var ConstraintsCByStatusDS = model.ConstraintsCByStatusArrDS.ListDS;
    for(var i=0;i<ConstraintsCByStatusArrRel.length;i++){
        s.specificationCode = ConstraintsCByStatusArrRel[i];
        var r = epc.getSpecificationByKey(s);
        if(!api_common.isFault(r)){
            ConstraintsCByStatusDS[i]=r;
            var SubOperationRel = r.SubOperation;
            s.specificationCode = SubOperationRel;
            var c =  epc.getSpecificationByKey(s);
            if(!api_common.isFault(c)){
                ConstraintsCByStatusDS[i].SubOperationDS = c;
            }
        }
    }
    return model;
}
